'use client';
import { useState } from 'react';
import { useRouter } from 'next/navigation';
import {  Grid3x3, List, ZoomIn } from 'lucide-react';


export default function ProductListPage() {
  const router = useRouter();
  
  const [products] = useState([
    {
      id: 1,
      name: 'Chini',
      price: 99.99,
      description: 'High-quality wireless headphones with noise cancellation and premium sound quality.',
      images: [
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,'
      ],
      
      unit: 'kg'
    },
    {
      id: 2,
      name: 'Rato Khursani',
      price: 299.99,
      description: 'Advanced fitness tracking, heart rate monitor, and smartphone notifications.',
      images: [
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSWm0-3140_xXY_iZHpTfnMDSK9CNl-NKO2XLL_0rOY7bVu-eRzmoD324GN7HAMoBwvygc&usqp=CAU',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTlqcTrIzPTxH9aBxYdOptwtWiNnJjyyIKwH45KoypoKkMqj6zXNJdayw0tDCPlLlAOClk&usqp=CAU',
        'https://prod.raramarket.jp/media/product/245/65815fccf67cd48e108ea7a7_red-chilli-dry-whole-nst-250gm.png',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT1FcTKD09Ap2HnxUWTVBVFR1HNRwsY8RrImsHeBA8c1q3tTDpqg3ePFoDy5_9mndXqRwI&usqp=CAU'
      ],
      
      unit: 'Kg'
    },
    {
      id: 3,
      name: 'Dhaniya',
      price: 49.99,
      description: 'Durable and stylish backpack with multiple compartments for laptop and accessories.',
      images: [
        'data:image/jpeg;base64,',
        'data:image/jpeg;base64,',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQGSojNEqSYYDhIuX0C9jMfswUXDtswcKgPmQ&s',
        'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS2Tya-3oZPIH2s21gf_Rb2FXRzl1lPSwGs9w&s'
      ],
     
      unit: 'Kg'
    }
  ]);

 
  const [viewMode, setViewMode] = useState('list');
  const [selectedImages, setSelectedImages] = useState({});
  const [magnifierPosition, setMagnifierPosition] = useState({ x: 0, y: 0 });
  const [showMagnifier, setShowMagnifier] = useState(false);

 

  const orderNow = (product) => {
    // Store product in localStorage and navigate to order page
    localStorage.setItem('selectedProduct', JSON.stringify(product));
    router.push('/business/order');
  };

  const selectImage = (productId, imageIndex) => {
    setSelectedImages({
      ...selectedImages,
      [productId]: imageIndex
    });
  };

  const handleMouseMove = (e) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = ((e.clientX - rect.left) / rect.width) * 100;
    const y = ((e.clientY - rect.top) / rect.height) * 100;
    setMagnifierPosition({ x, y });
  };

  const getCurrentImage = (product) => {
    const index = selectedImages[product.id] || 0;
    return product.images[index];
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-4">
          <div className="flex items-center justify-between">
            <h1 className="text-3xl font-bold text-gray-900">Our Products</h1>
            <div className="flex items-center gap-4">
              
             <button
  onClick={() => router.push('/business/order-status')}
  className="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition-colors font-semibold"
>
  My Order
</button>

            </div>
          </div>
        </div>
      </header>

      {/* Product Grid/List */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="mb-6 flex items-center justify-between">
          <p className="text-gray-600"></p>
          
          {/* View Toggle Buttons */}
          <div className="flex gap-2">
            <button
              onClick={() => setViewMode('list')}
              className={`p-2 rounded-lg transition-colors ${
                viewMode === 'list'
                  ? 'bg-indigo-600 text-white'
                  : 'bg-white text-gray-600 hover:bg-gray-100'
              }`}
            >
              <List className="w-5 h-5" />
            </button>
            <button
              onClick={() => setViewMode('grid')}
              className={`p-2 rounded-lg transition-colors ${
                viewMode === 'grid'
                  ? 'bg-indigo-600 text-white'
                  : 'bg-white text-gray-600 hover:bg-gray-100'
              }`}
            >
              <Grid3x3 className="w-5 h-5" />
            </button>
          </div>
        </div>

        {/* List View */}
        {viewMode === 'list' && (
          <div className="space-y-4">
            {products.map((product) => (
              <div
                key={product.id}
                className="bg-white rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300 overflow-hidden"
              >
                <div className="flex flex-col sm:flex-row">
                  {/* Product Images */}
                  <div className="w-full sm:w-80 shrink-0 p-4">
                    {/* Main Image with Magnifier */}
                    <div 
                      className="relative w-full h-64 bg-gray-200 rounded-lg overflow-hidden mb-3 cursor-crosshair"
                      onMouseMove={handleMouseMove}
                      onMouseEnter={() => setShowMagnifier(true)}
                      onMouseLeave={() => setShowMagnifier(false)}
                    >
                      <img
                        src={getCurrentImage(product)}
                        alt={product.name}
                        className="w-full h-full object-cover"
                      />
                      {showMagnifier && (
                        <div className="absolute inset-0 pointer-events-none">
                          <div 
                            className="absolute w-32 h-32 border-2 border-white rounded-full shadow-lg overflow-hidden"
                            style={{
                              left: `${magnifierPosition.x}%`,
                              top: `${magnifierPosition.y}%`,
                              transform: 'translate(-50%, -50%)',
                              backgroundImage: `url(${getCurrentImage(product)})`,
                              backgroundSize: '300%',
                              backgroundPosition: `${magnifierPosition.x}% ${magnifierPosition.y}%`
                            }}
                          />
                        </div>
                      )}
                      <div className="absolute top-3 right-3 bg-white rounded-full p-2 shadow-md">
                        <ZoomIn className="w-4 h-4 text-gray-600" />
                      </div>
                      
                    </div>
                    
                    {/* Thumbnail Images */}
                    <div className="flex gap-2">
                      {product.images.map((image, index) => (
                        <button
                          key={index}
                          onClick={() => selectImage(product.id, index)}
                          className={`w-16 h-16 rounded-lg overflow-hidden border-2 transition-all ${
                            (selectedImages[product.id] || 0) === index
                              ? 'border-indigo-600 scale-105'
                              : 'border-gray-300 hover:border-gray-400'
                          }`}
                        >
                          <img
                            src={image}
                            alt={`${product.name} ${index + 1}`}
                            className="w-full h-full object-cover"
                          />
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Product Details */}
                  <div className="flex-1 p-5 flex flex-col justify-between">
                    <div>
                      <h3 className="text-2xl font-semibold text-gray-900 mb-2">
                        {product.name}
                      </h3>

                      {/* Rating */}


                      {/* Description */}
                      <p className="text-gray-600 mb-4">
                        {product.description}
                      </p>
                    </div>

                    {/* Price and Order Button */}
                    <div className="flex items-center justify-between">
                      <span className="text-3xl font-bold text-indigo-600">
                        ${product.price}
                      </span>
                      <button
                        onClick={() => orderNow(product)}
                        className="bg-indigo-600 text-white px-6 py-3 rounded-lg hover:bg-indigo-700 transition-colors font-semibold"
                      >
                        Order Now
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Grid View */}
        {viewMode === 'grid' && (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {products.map((product) => (
              <div
                key={product.id}
                className="bg-white rounded-lg shadow-md hover:shadow-xl transition-shadow duration-300 overflow-hidden group"
              >
                {/* Product Images */}
                <div className="p-4">
                  {/* Main Image with Magnifier */}
                  <div 
                    className="relative overflow-hidden bg-gray-200 h-64 rounded-lg mb-3 cursor-crosshair"
                    onMouseMove={handleMouseMove}
                    onMouseEnter={() => setShowMagnifier(true)}
                    onMouseLeave={() => setShowMagnifier(false)}
                  >
                    <img
                      src={getCurrentImage(product)}
                      alt={product.name}
                      className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-300"
                    />
                    {showMagnifier && (
                      <div className="absolute inset-0 pointer-events-none">
                        <div 
                          className="absolute w-32 h-32 border-2 border-white rounded-full shadow-lg overflow-hidden"
                          style={{
                            left: `${magnifierPosition.x}%`,
                            top: `${magnifierPosition.y}%`,
                            transform: 'translate(-50%, -50%)',
                            backgroundImage: `url(${getCurrentImage(product)})`,
                            backgroundSize: '300%',
                            backgroundPosition: `${magnifierPosition.x}% ${magnifierPosition.y}%`
                          }}
                        />
                      </div>
                    )}
                    <div className="absolute top-3 right-3 bg-white rounded-full p-2 shadow-md">
                      <ZoomIn className="w-4 h-4 text-gray-600" />
                    </div>
                    
                  </div>
                  
                  {/* Thumbnail Images */}
                  <div className="flex gap-2 justify-center">
                    {product.images.map((image, index) => (
                      <button
                        key={index}
                        onClick={() => selectImage(product.id, index)}
                        className={`w-14 h-14 rounded-lg overflow-hidden border-2 transition-all ${
                          (selectedImages[product.id] || 0) === index
                            ? 'border-indigo-600 scale-105'
                            : 'border-gray-300 hover:border-gray-400'
                        }`}
                      >
                        <img
                          src={image}
                          alt={`${product.name} ${index + 1}`}
                          className="w-full h-full object-cover"
                        />
                      </button>
                    ))}
                  </div>
                </div>

                {/* Product Details */}
                <div className="px-5 pb-5">
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    {product.name}
                  </h3>

                  {/* Rating */}
                

                  {/* Description */}
                  <p className="text-gray-600 text-sm mb-4 line-clamp-2">
                    {product.description}
                  </p>

                  {/* Price and Order Button */}
                  <div className="flex items-center justify-between">
                    <span className="text-2xl font-bold text-indigo-600">
                      NRs. {product.price}
                    </span>
                    <button
                      onClick={() => orderNow(product)}
                      className="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition-colors font-semibold"
                    >
                      Order Now
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>
    </div>
  );
}